import React, { useState } from 'react';

function ProjectDetails({ project, onBack }) {
  
  const [likes, setLikes] = useState(project.views || 0);
  const [isLiked, setIsLiked] = useState(false);
  
  // 1. CAROUSEL STATE FOR DETAILS PAGE
  const [activeImageIndex, setActiveImageIndex] = useState(0);

  // 2. CREATE A VIRTUAL GALLERY
  // (We use the Real Image if it exists, PLUS the Icon as a secondary slide)
  const gallery = [];
  if (project.imageUrl) gallery.push({ type: 'image', content: project.imageUrl });
  gallery.push({ type: 'icon', content: project.icon || 'üì¶', color: project.color });
  // You could add a fake 3rd slide here if you wanted, e.g., a "Code Snippet" placeholder.

  const nextImage = () => setActiveImageIndex((prev) => (prev + 1) % gallery.length);
  const prevImage = () => setActiveImageIndex((prev) => (prev - 1 + gallery.length) % gallery.length);

  const handleLike = () => {
    if (isLiked) {
      setLikes(likes - 1);
      setIsLiked(false);
    } else {
      setLikes(likes + 1);
      setIsLiked(true);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 text-white p-6">
      
      {/* BACK BUTTON */}
      <button 
        onClick={onBack}
        className="mb-8 flex items-center gap-2 text-blue-200 hover:text-white transition font-bold"
      >
        ‚Üê Back to Browse
      </button>

      {/* MAIN GRID */}
      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
        
        {/* LEFT SIDE: MULTI-IMAGE CAROUSEL */}
        <div className="lg:col-span-2 space-y-6">
          <div className="aspect-video rounded-3xl bg-gray-800 shadow-2xl relative overflow-hidden group border border-white/10 flex items-center justify-center">
             
             {/* RENDER ACTIVE SLIDE */}
             {gallery[activeImageIndex].type === 'image' ? (
               <img 
                 src={gallery[activeImageIndex].content} 
                 alt="Project Slide" 
                 className="w-full h-full object-cover animate-fadeIn"
               />
             ) : (
               <div className={`w-full h-full bg-gradient-to-br ${gallery[activeImageIndex].color || 'from-gray-700 to-gray-900'} flex items-center justify-center animate-fadeIn`}>
                  <div className="text-9xl transform scale-110">
                    {gallery[activeImageIndex].content}
                  </div>
               </div>
             )}

             {/* CAROUSEL CONTROLS (Only show if more than 1 slide) */}
             {gallery.length > 1 && (
               <>
                 <button onClick={prevImage} className="absolute left-4 top-1/2 -translate-y-1/2 w-10 h-10 rounded-full bg-black/40 hover:bg-black/60 text-white flex items-center justify-center backdrop-blur-md transition">‚ùÆ</button>
                 <button onClick={nextImage} className="absolute right-4 top-1/2 -translate-y-1/2 w-10 h-10 rounded-full bg-black/40 hover:bg-black/60 text-white flex items-center justify-center backdrop-blur-md transition">‚ùØ</button>
                 
                 {/* DOTS */}
                 <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex gap-2">
                   {gallery.map((_, idx) => (
                     <div 
                       key={idx} 
                       onClick={() => setActiveImageIndex(idx)}
                       className={`w-2 h-2 rounded-full cursor-pointer transition-all ${idx === activeImageIndex ? 'bg-white w-4' : 'bg-white/40'}`}
                     />
                   ))}
                 </div>
               </>
             )}

          </div>
          
          {/* IMAGE THUMBNAILS (Visual Indicator of slides) */}
          <div className="flex gap-4 overflow-x-auto pb-2">
            {gallery.map((item, idx) => (
              <div 
                key={idx}
                onClick={() => setActiveImageIndex(idx)}
                className={`w-24 h-16 rounded-lg overflow-hidden cursor-pointer border-2 transition ${idx === activeImageIndex ? 'border-blue-400 opacity-100' : 'border-transparent opacity-50 hover:opacity-100'}`}
              >
                {item.type === 'image' ? (
                  <img src={item.content} alt="thumb" className="w-full h-full object-cover" />
                ) : (
                  <div className={`w-full h-full bg-gradient-to-br ${item.color || 'from-gray-600 to-gray-800'} flex items-center justify-center text-2xl`}>
                    {item.content}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* RIGHT SIDE: INFO */}
        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-xl border border-white/20 p-8 rounded-3xl shadow-xl">
            
            <span className="bg-blue-500/20 text-blue-300 px-3 py-1 rounded-full text-xs font-bold uppercase tracking-wider mb-4 inline-block">
              {project.category}
            </span>
            
            <h1 className="text-4xl font-bold mb-2">{project.title}</h1>
            <p className="text-lg text-white/60 mb-6">Created by {project.author}</p>

            <div className="flex gap-4 mb-8">
              <button 
                className="flex-1 bg-white text-indigo-900 font-bold py-4 rounded-xl hover:bg-blue-50 transition shadow-lg hover:scale-[1.02]"
                onClick={() => {
                  if (project.link) {
                    window.open(project.link, "_blank");
                  } else {
                    alert("No link provided.");
                  }
                }}
              >
                Launch App üöÄ
              </button>
              
              <button 
                onClick={handleLike}
                className={`px-6 rounded-xl font-bold border border-white/20 transition flex items-center gap-2 ${isLiked ? 'bg-pink-500 border-pink-500 text-white' : 'bg-white/5 hover:bg-white/10'}`}
              >
                {isLiked ? '‚ù§Ô∏è' : 'ü§ç'} {likes}
              </button>
            </div>

            <hr className="border-white/10 mb-6" />

            <h3 className="text-xl font-bold mb-3">About this Project</h3>
            <p className="text-blue-100 leading-relaxed whitespace-pre-wrap">
              {project.description || "No description provided."}
            </p>

          </div>
        </div>

      </div>
    </div>
  );
}

export default ProjectDetails;
