import React, { useState, useEffect } from 'react';
// 1. Import Firebase tools
import { db } from '../firebase';
import { collection, getDocs, doc, updateDoc } from 'firebase/firestore';

function AdminPanel() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // 2. Fetch All Projects from Cloud
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "projects"));
        const projectsList = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setProjects(projectsList);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching projects:", error);
      }
    };

    fetchProjects();
  }, []);

  // 3. The "Approve/Reject" Logic
  const handleStatusUpdate = async (id, newStatus) => {
    try {
      // A. Update in Google Database
      const projectRef = doc(db, "projects", id);
      await updateDoc(projectRef, { status: newStatus });

      // B. Update locally (so the screen changes instantly without refresh)
      setProjects(projects.map(p => (p.id === id ? { ...p, status: newStatus } : p)));
      
    } catch (error) {
      console.error("Error updating status:", error);
      alert("Failed to update status");
    }
  };

  // Helper: Filter projects by status
  const pendingCount = projects.filter(p => !p.status || p.status === 'Pending').length;
  const approvedCount = projects.filter(p => p.status === 'Approved').length;
  const rejectedCount = projects.filter(p => p.status === 'Rejected').length;

  return (
    <div className="flex h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 text-white overflow-hidden">
      
      {/* SIDEBAR */}
      <aside className="w-64 bg-white/5 backdrop-blur-xl border-r border-white/10 hidden md:flex flex-col p-6">
        <h2 className="text-2xl font-bold tracking-tight mb-10 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400">AdminConsole</h2>
        <nav className="space-y-4">
          <div className="bg-white/10 p-3 rounded-xl cursor-pointer font-semibold text-blue-200">ðŸ“Š Dashboard</div>
          <div className="p-3 rounded-xl cursor-pointer hover:bg-white/5 text-gray-400">ðŸ“‚ All Projects</div>
          <div className="p-3 rounded-xl cursor-pointer hover:bg-white/5 text-gray-400">ðŸ‘¥ Users</div>
        </nav>
      </aside>

      {/* MAIN CONTENT */}
      <main className="flex-1 p-8 overflow-y-auto">
        <header className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold">Overview</h1>
            <p className="text-gray-400">Manage student submissions.</p>
          </div>
        </header>

        {/* STATS CARDS */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
          <div className="bg-white/5 backdrop-blur-md p-6 rounded-2xl border border-white/10">
            <h3 className="text-gray-400 mb-2">Pending Reviews</h3>
            <p className="text-4xl font-bold text-yellow-400">{pendingCount}</p>
          </div>
          <div className="bg-white/5 backdrop-blur-md p-6 rounded-2xl border border-white/10">
            <h3 className="text-gray-400 mb-2">Total Approved</h3>
            <p className="text-4xl font-bold text-green-400">{approvedCount}</p>
          </div>
          <div className="bg-white/5 backdrop-blur-md p-6 rounded-2xl border border-white/10">
            <h3 className="text-gray-400 mb-2">Total Rejected</h3>
            <p className="text-4xl font-bold text-red-400">{rejectedCount}</p>
          </div>
        </div>

        {/* INBOX TABLE */}
        <div className="bg-white/5 backdrop-blur-md rounded-2xl border border-white/10 overflow-hidden">
          <div className="p-6 border-b border-white/10">
            <h3 className="text-xl font-bold">Project Inbox</h3>
          </div>
          
          <div className="overflow-x-auto">
            {loading ? (
              <div className="p-10 text-center text-gray-400">Loading Dashboard...</div>
            ) : (
              <table className="w-full text-left">
                <thead className="bg-white/5 text-gray-400">
                  <tr>
                    <th className="p-4">Project Name</th>
                    <th className="p-4">Category</th>
                    <th className="p-4">Status</th>
                    <th className="p-4">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-white/5">
                  {projects.map((project) => (
                    <tr key={project.id} className="hover:bg-white/5 transition">
                      <td className="p-4 font-semibold">
                        {project.title} <br/>
                        <span className="text-xs text-gray-500 font-normal">{project.link}</span>
                      </td>
                      <td className="p-4"><span className="bg-white/10 px-2 py-1 rounded text-sm">{project.category}</span></td>
                      <td className="p-4">
                        <span className={`px-3 py-1 rounded-full text-xs font-bold 
                          ${!project.status || project.status === 'Pending' ? 'bg-yellow-500/20 text-yellow-300' : 
                            project.status === 'Approved' ? 'bg-green-500/20 text-green-300' : 'bg-red-500/20 text-red-300'}`}>
                          {project.status || 'Pending'}
                        </span>
                      </td>
                      <td className="p-4 flex gap-2">
                        {/* Only show buttons if not already processed */}
                        {(!project.status || project.status === 'Pending') && (
                          <>
                            <button 
                              onClick={() => handleStatusUpdate(project.id, 'Approved')}
                              className="p-2 bg-green-500/20 text-green-400 rounded-lg hover:bg-green-500 hover:text-white transition">
                              âœ” Approve
                            </button>
                            <button 
                              onClick={() => handleStatusUpdate(project.id, 'Rejected')}
                              className="p-2 bg-red-500/20 text-red-400 rounded-lg hover:bg-red-500 hover:text-white transition">
                              âœ– Reject
                            </button>
                          </>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>

      </main>
    </div>
  );
}

export default AdminPanel;
