import React, { useState } from 'react';
// 1. Import Firebase tools
import { db } from '../firebase';
import { collection, addDoc } from 'firebase/firestore';

function AddProject({ onBack }) {
  const [step, setStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false); // Loading state

  const [formData, setFormData] = useState({
    title: '',
    category: 'Web App',
    description: '',
    link: '',
    author: 'Student (23bsccs01)', // Hardcoded for now
    views: 0,
    color: 'from-blue-500 to-indigo-500', // Default gradient
    icon: 'üöÄ' // Default icon
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // 2. The Real Submit Function
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Send data to the "projects" collection in Firestore
      await addDoc(collection(db, "projects"), formData);
      
      alert("Success! Project saved to the Cloud. ‚òÅÔ∏è");
      onBack(); // Go back home so they can see it
    } catch (error) {
      console.error("Error adding document: ", error);
      alert("Error saving project. Check console.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 flex items-center justify-center p-4">
      
      <div className="w-full max-w-2xl bg-white/10 backdrop-blur-xl border border-white/20 rounded-3xl p-8 text-white shadow-2xl relative">
        <button onClick={onBack} className="absolute top-4 right-4 text-white/50 hover:text-white text-2xl">&times;</button>

        {/* PROGRESS BAR */}
        <div className="mb-8">
          <div className="flex justify-between text-sm mb-2 text-blue-200">
            <span>Step 1: Basic Info</span>
            <span>Step 2: Links & Media</span>
            <span>Step 3: Review</span>
          </div>
          <div className="h-2 bg-black/20 rounded-full overflow-hidden">
            <div className="h-full bg-gradient-to-r from-blue-400 to-purple-400 transition-all duration-500" style={{ width: `${(step / 3) * 100}%` }}></div>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          
          {/* STEP 1 */}
          {step === 1 && (
            <div className="space-y-6 animate-fadeIn">
              <h2 className="text-3xl font-bold">Tell us about your project</h2>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Project Title</label>
                <input name="title" value={formData.title} onChange={handleChange} className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition" placeholder="e.g. AI Plant Detector" required />
              </div>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Category</label>
                <select name="category" value={formData.category} onChange={handleChange} className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition [&>option]:text-black">
                  <option>Web App</option>
                  <option>Mobile App</option>
                  <option>Game</option>
                  <option>AI / ML</option>
                </select>
              </div>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Description</label>
                <textarea name="description" value={formData.description} onChange={handleChange} rows="3" className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition" placeholder="What does it do?" required />
              </div>
              <button type="button" onClick={() => setStep(2)} className="w-full bg-blue-600 py-4 rounded-xl font-bold hover:bg-blue-500 transition">Next Step ‚ûú</button>
            </div>
          )}

          {/* STEP 2 */}
          {step === 2 && (
            <div className="space-y-6 animate-fadeIn">
              <h2 className="text-3xl font-bold">Where is it hosted?</h2>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Project Link (URL)</label>
                <input name="link" value={formData.link} onChange={handleChange} className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition" placeholder="https://github.com/..." required />
              </div>
              <div className="flex gap-4">
                <button type="button" onClick={() => setStep(1)} className="w-1/3 bg-white/10 py-4 rounded-xl font-bold hover:bg-white/20 transition">‚¨Ö Back</button>
                <button type="button" onClick={() => setStep(3)} className="w-2/3 bg-blue-600 py-4 rounded-xl font-bold hover:bg-blue-500 transition">Review ‚ûú</button>
              </div>
            </div>
          )}

          {/* STEP 3 */}
          {step === 3 && (
            <div className="space-y-6 animate-fadeIn text-center">
              <h2 className="text-3xl font-bold">Ready to Launch?</h2>
              <div className="bg-white/10 p-6 rounded-2xl text-left border border-white/20">
                <h3 className="text-2xl font-bold mb-1">{formData.title}</h3>
                <span className="text-xs bg-blue-500 px-2 py-1 rounded uppercase tracking-wider">{formData.category}</span>
                <p className="mt-4 text-blue-100">{formData.description}</p>
                <div className="mt-4 text-sm text-white/50 truncate">üîó {formData.link}</div>
              </div>

              <div className="flex gap-4">
                <button type="button" onClick={() => setStep(2)} className="w-1/3 bg-white/10 py-4 rounded-xl font-bold hover:bg-white/20 transition">‚¨Ö Edit</button>
                <button 
                  type="submit" 
                  disabled={isSubmitting}
                  className="w-2/3 bg-green-500 py-4 rounded-xl font-bold hover:bg-green-400 transition shadow-lg shadow-green-500/20 disabled:opacity-50"
                >
                  {isSubmitting ? "Uploading... ‚è≥" : "üöÄ Submit Project"}
                </button>
              </div>
            </div>
          )}

        </form>
      </div>
    </div>
  );
}

export default AddProject;
