import React, { useState, useEffect } from 'react';
import { db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';

function StudentHome({ onNavigate, onViewProject, onLogout }) { 
  const [searchTerm, setSearchTerm] = useState('');
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  
  // 1. CAROUSEL STATE: Keeps track of which slide to show (0, 1, 2...)
  const [currentSlide, setCurrentSlide] = useState(0);

  // FETCH DATA
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "projects"));
        const projectsList = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setProjects(projectsList);
        setLoading(false);
      } catch (error) {
        console.error("Error loading projects:", error);
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  // 2. CAROUSEL TIMER: Switches the slide every 4 seconds
  useEffect(() => {
    if (projects.length > 0) {
      const interval = setInterval(() => {
        // Go to next slide, or loop back to 0 if at the end
        setCurrentSlide((prev) => (prev + 1) % projects.length);
      }, 4000); // 4000ms = 4 seconds

      return () => clearInterval(interval); // Cleanup when page closes
    }
  }, [projects]);

  const filteredProjects = projects.filter(p => 
    p.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Helper to get the current "Featured" project (or a default if loading)
  const heroProject = projects.length > 0 ? projects[currentSlide] : {
    title: "Loading Projects...",
    description: "Please wait while we fetch the latest data.",
    color: "from-gray-700 to-gray-900",
    icon: "‚è≥"
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 text-white pb-20">
      
      {/* NAVBAR */}
      <nav className="sticky top-0 z-50 bg-white/10 backdrop-blur-md border-b border-white/10 px-6 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold tracking-tight">CampusCanvas</h1>
        
        <div className="hidden md:flex bg-black/20 rounded-full px-4 py-2 border border-white/10 w-96">
          <span className="mr-2">üîç</span>
          <input 
            type="text" 
            placeholder="Search projects..." 
            className="bg-transparent outline-none w-full text-sm placeholder-white/50"
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="flex items-center gap-4">
          <button 
            onClick={() => onNavigate('add-project')}
            className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center font-bold hover:bg-blue-400 transition shadow-lg shadow-blue-500/50 text-white text-2xl pb-1"
            title="Upload Project"
          >
            +
          </button>
          
          <button 
            onClick={onLogout}
            className="bg-red-500/20 text-red-300 px-4 py-2 rounded-full text-sm font-bold hover:bg-red-500 hover:text-white transition"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* 3. DYNAMIC HERO CAROUSEL */}
      <div className="max-w-7xl mx-auto mt-10 px-6">
        <div className={`relative overflow-hidden rounded-3xl bg-gradient-to-r ${heroProject.color || 'from-violet-600 to-indigo-600'} p-8 md:p-12 shadow-2xl border border-white/20 transition-all duration-1000`}>
          
          {/* Animated Background Blob */}
          <div className="absolute top-0 right-0 -mr-20 -mt-20 w-80 h-80 bg-white/10 rounded-full blur-3xl animate-pulse"></div>
          
          <div className="relative z-10 max-w-2xl">
            <div className="flex items-center gap-3 mb-4">
              <span className="bg-white/20 px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wider">Featured Project</span>
              {/* Pagination Dots */}
              <div className="flex gap-1">
                {projects.slice(0, 5).map((_, idx) => (
                  <div key={idx} className={`w-2 h-2 rounded-full transition-all ${idx === currentSlide ? 'bg-white w-4' : 'bg-white/30'}`}></div>
                ))}
              </div>
            </div>

            <h2 className="text-4xl md:text-5xl font-bold mb-4 transition-all duration-500">{heroProject.title}</h2>
            <p className="text-lg text-blue-100 mb-8 line-clamp-2">{heroProject.description || "Explore this amazing project by a fellow student."}</p>
            
            <button 
               onClick={() => onViewProject(heroProject)}
               className="bg-white text-indigo-900 px-8 py-3 rounded-full font-bold hover:bg-blue-50 transition shadow-lg hover:scale-105 active:scale-95"
            >
              Check it out üöÄ
            </button>
          </div>
        </div>
      </div>

      {/* TRENDING GRID */}
      <div className="max-w-7xl mx-auto mt-12 px-6">
        <h3 className="text-2xl font-bold mb-6 flex items-center">üî• All Projects</h3>
        
        {loading ? (
           <div className="text-center py-20 text-white/50">Loading cloud data...</div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {filteredProjects.map((project) => (
              <div key={project.id} className="group bg-white/5 backdrop-blur-lg border border-white/10 rounded-2xl p-5 hover:bg-white/10 transition cursor-pointer">
                <div className={`h-32 rounded-xl bg-gradient-to-br ${project.color || 'from-gray-500 to-gray-700'} flex items-center justify-center text-6xl shadow-inner mb-4 group-hover:scale-105 transition duration-300`}>
                  {project.icon || 'üì¶'}
                </div>
                <h4 className="text-xl font-bold truncate">{project.title}</h4>
                <p className="text-sm text-white/60 mb-4">{project.category} ‚Ä¢ by {project.author?.split('(')[0]}</p>
                <div className="flex justify-between items-center border-t border-white/10 pt-4">
                  <span className="text-xs bg-white/10 px-2 py-1 rounded">üëÄ {project.views || 0}</span>
                  <button 
                    onClick={() => onViewProject(project)} 
                    className="text-sm font-bold text-blue-300 hover:text-white transition"
                  >
                    View ‚ûú
                  </button>
                </div>
              </div>
            ))}
            
            {projects.length === 0 && (
               <div className="col-span-full text-center text-white/50 py-10">No projects found. Be the first to upload!</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default StudentHome;
