import React, { useState } from 'react';

import Login from './pages/Login';
import StudentHome from './pages/StudentHome';


function App() {
  // This state remembers who is logged in
  // Options: 'login', 'student', 'admin'
  const [userRole, setUserRole] = useState('login');

  const handleLogin = (role) => {
    setUserRole(role); // Switches the screen
  };

  return (
    <div className="App">
      {/* ðŸš¦ Traffic Control Logic */}
      
      {userRole === 'login' && <Login onLogin={handleLogin} />}
      
      {userRole === 'student' && <StudentHome />}
      
      {userRole === 'admin' && (
        <div className="text-center mt-20 text-2xl">Admin Dashboard (Coming Soon...)</div>
      )}
      
    </div>
  );
}

export default App;
