import React, { useState } from 'react';
import { db } from '../firebase'; 
import { collection, addDoc } from 'firebase/firestore';

function AddProject({ onBack }) {
  const [step, setStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  // State for the file
  const [previewUrl, setPreviewUrl] = useState(null);
  const [imageBase64, setImageBase64] = useState(null);

  const user = JSON.parse(localStorage.getItem('user_info')) || { name: 'Student', id: 'Anonymous' };

  const [formData, setFormData] = useState({
    title: '',
    category: 'Web App',
    description: '',
    link: '',
    author: `${user.name} (${user.id})`, 
    views: 0,
    color: 'from-blue-500 to-indigo-500',
    icon: 'üöÄ',
    status: 'Pending' // Explicitly set status to Pending
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // 1. IMAGE HANDLER (With Size Limit)
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // üîí SIZE CHECK: Limit to ~800KB
      if (file.size > 800 * 1024) {
        alert("File too large! Please choose an image smaller than 1MB.");
        return; // Stop here
      }

      // Show preview
      setPreviewUrl(URL.createObjectURL(file));

      // Convert to Base64 Text
      const reader = new FileReader();
      reader.onloadend = () => {
        setImageBase64(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // 2. SAVE TO FIRESTORE
      await addDoc(collection(db, "projects"), {
        ...formData,
        imageUrl: imageBase64 || "" // Save the text string
      });
      
      alert("Success! Project submitted for approval. ‚òÅÔ∏è");
      onBack();
    } catch (error) {
      console.error("Error adding document: ", error);
      alert("Error saving project. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white/10 backdrop-blur-xl border border-white/20 rounded-3xl p-8 text-white shadow-2xl relative">
        <button onClick={onBack} className="absolute top-4 right-4 text-white/50 hover:text-white text-2xl">&times;</button>

        {/* PROGRESS BAR */}
        <div className="mb-8">
          <div className="flex justify-between text-sm mb-2 text-blue-200">
            <span>Step 1: Basic Info</span>
            <span>Step 2: Media</span>
            <span>Step 3: Review</span>
          </div>
          <div className="h-2 bg-black/20 rounded-full overflow-hidden">
            <div className="h-full bg-gradient-to-r from-blue-400 to-purple-400 transition-all duration-500" style={{ width: `${(step / 3) * 100}%` }}></div>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          
          {/* STEP 1 */}
          {step === 1 && (
            <div className="space-y-6 animate-fadeIn">
              <h2 className="text-3xl font-bold">Project Info</h2>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Project Title</label>
                <input name="title" value={formData.title} onChange={handleChange} className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition" placeholder="e.g. Campus Bot" required />
              </div>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Category</label>
                <select name="category" value={formData.category} onChange={handleChange} className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition [&>option]:text-black">
                  <option>Web App</option>
                  <option>Mobile App</option>
                  <option>Game</option>
                  <option>AI / ML</option>
                </select>
              </div>
              <div>
                <label className="block text-sm text-blue-200 mb-2">Description</label>
                <textarea name="description" value={formData.description} onChange={handleChange} rows="3" className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition" placeholder="Short summary..." required />
              </div>
              <button type="button" onClick={() => setStep(2)} className="w-full bg-blue-600 py-4 rounded-xl font-bold hover:bg-blue-500 transition">Next ‚ûú</button>
            </div>
          )}

          {/* STEP 2: IMAGE UPLOAD */}
          {step === 2 && (
            <div className="space-y-6 animate-fadeIn">
              <h2 className="text-3xl font-bold">Add Screenshot</h2>
              
              <div className="relative border-2 border-dashed border-white/20 rounded-2xl p-8 text-center hover:bg-white/5 transition group">
                <input 
                  type="file" 
                  accept="image/*"
                  onChange={handleImageChange}
                  className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                />
                
                {previewUrl ? (
                   <img src={previewUrl} alt="Preview" className="h-48 mx-auto rounded-lg shadow-lg object-cover" />
                ) : (
                  <>
                    <span className="text-4xl block mb-2 group-hover:scale-110 transition">üì∏</span>
                    <p className="text-sm text-blue-200">Click to Upload Thumbnail</p>
                    <p className="text-xs text-orange-300 mt-2">Note: Please use small images (under 1MB)</p>
                  </>
                )}
              </div>

              <div>
                <label className="block text-sm text-blue-200 mb-2">Project Link (URL)</label>
                <input name="link" value={formData.link} onChange={handleChange} className="w-full bg-white/5 border border-white/10 rounded-xl p-4 text-lg focus:outline-none focus:border-blue-400 transition" placeholder="https://..." required />
              </div>

              <div className="flex gap-4">
                <button type="button" onClick={() => setStep(1)} className="w-1/3 bg-white/10 py-4 rounded-xl font-bold hover:bg-white/20 transition">‚¨Ö Back</button>
                <button type="button" onClick={() => setStep(3)} className="w-2/3 bg-blue-600 py-4 rounded-xl font-bold hover:bg-blue-500 transition">Review ‚ûú</button>
              </div>
            </div>
          )}

          {/* STEP 3 */}
          {step === 3 && (
            <div className="space-y-6 animate-fadeIn text-center">
              <h2 className="text-3xl font-bold">Ready?</h2>
              
              <div className="bg-white/10 p-6 rounded-2xl text-left border border-white/20 flex gap-4 items-center">
                 <div className="w-16 h-16 bg-black/20 rounded-lg flex items-center justify-center overflow-hidden">
                    {previewUrl ? <img src={previewUrl} alt="mini" className="w-full h-full object-cover" /> : "üöÄ"}
                 </div>
                 <div>
                    <h3 className="text-xl font-bold">{formData.title}</h3>
                    <p className="text-sm text-white/50">{formData.category}</p>
                 </div>
              </div>

              <div className="flex gap-4">
                <button type="button" onClick={() => setStep(2)} className="w-1/3 bg-white/10 py-4 rounded-xl font-bold hover:bg-white/20 transition">‚¨Ö Edit</button>
                <button type="submit" disabled={isSubmitting} className="w-2/3 bg-green-500 py-4 rounded-xl font-bold hover:bg-green-400 transition shadow-lg disabled:opacity-50">
                  {isSubmitting ? "Saving... ‚è≥" : "üöÄ Launch"}
                </button>
              </div>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

