import React, { useState } from 'react';
import AdminPanel from './pages/AdminPanel';
import Login from './pages/Login';
import StudentHome from './pages/StudentHome';
import AddProject from './pages/AddProject';
import ProjectDetails from './pages/ProjectDetails';




function App() {
  // This state remembers who is logged in
  // Options: 'login', 'student', 'admin'
  const [userRole, setUserRole] = useState('login');
  const [selectedProject, setSelectedProject] = useState(null);

  const handleLogin = (role) => {
    setUserRole(role); // Switches the screen
  };
  const handleViewProject = (project) => {
  setSelectedProject(project);
  setUserRole('view-project');
};

return (
    <div className="App">
      {/* LOGIN PAGE */}
      {userRole === 'login' && <Login onLogin={handleLogin} />}
      
      {/* STUDENT HOME (Pass the function to switch to 'add-project') */}
      {userRole === 'student' && <StudentHome onNavigate={handleLogin} />}
      
      {/* ADD PROJECT PAGE (Pass function to go back to 'student') */}
      {userRole === 'add-project' && <AddProject onBack={() => handleLogin('student')} />}
      
      {/* ADMIN DASHBOARD */}
      {userRole === 'admin' && <AdminPanel />}
      
    </div>
  );
}

export default App;
