import React, { useState, useEffect } from 'react';

// Import Pages
import Login from './pages/Login';
import StudentHome from './pages/StudentHome';
import AdminPanel from './pages/AdminPanel';
import AddProject from './pages/AddProject';
import ProjectDetails from './pages/ProjectDetails';

function App() {
  // 1. INITIALIZE STATE FROM LOCAL STORAGE
  // We check if there is a saved role in the browser's memory first.
  const [userRole, setUserRole] = useState(localStorage.getItem('appRole') || 'login');
  const [selectedProject, setSelectedProject] = useState(null);

  // 2. THE MEMORY KEEPER
  // Whenever 'userRole' changes, save it to the browser.
  useEffect(() => {
    localStorage.setItem('appRole', userRole);
  }, [userRole]);

  // 3. NAVIGATION HANDLER
  const handleNavigate = (page) => {
    setUserRole(page);
  };

  // 4. LOGOUT HANDLER (Clears memory)
  const handleLogout = () => {
    setUserRole('login');
    setSelectedProject(null);
    localStorage.removeItem('appRole'); // Delete the memory
  };

  const handleViewProject = (project) => {
    setSelectedProject(project);
    setUserRole('view-project');
  };

  return (
    <div className="App">
      
      {/* LOGIN */}
      {userRole === 'login' && (
        <Login onLogin={(role) => setUserRole(role)} />
      )}
      
      {/* STUDENT HOME (Pass handleLogout) */}
      {userRole === 'student' && (
        <StudentHome 
          onNavigate={handleNavigate} 
          onViewProject={handleViewProject}
          onLogout={handleLogout} // <--- Pass the logout tool
        />
      )}
      
      {/* ADD PROJECT */}
      {userRole === 'add-project' && (
        <AddProject onBack={() => setUserRole('student')} />
      )}

      {/* PROJECT DETAILS */}
      {userRole === 'view-project' && selectedProject && (
        <ProjectDetails 
          project={selectedProject} 
          onBack={() => setUserRole('student')} 
        />
      )}
      
      {/* ADMIN (Pass handleLogout) */}
      {userRole === 'admin' && (
        <AdminPanel onLogout={handleLogout} /> 
      )}

    </div>
  );
}

export default App;
