import React, { useState } from 'react';

function ProjectDetails({ project, onBack }) {
  const [likes, setLikes] = useState(project.views || 0);
  const [isLiked, setIsLiked] = useState(false);

  const handleLike = () => {
    if (isLiked) {
      setLikes(likes - 1);
      setIsLiked(false);
    } else {
      setLikes(likes + 1);
      setIsLiked(true);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 text-white p-6">
      
      {/* NAV / BACK BUTTON */}
      <button 
        onClick={onBack}
        className="mb-8 flex items-center gap-2 text-blue-200 hover:text-white transition"
      >
        ‚Üê Back to Browse
      </button>

      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
        
        {/* LEFT COLUMN: Main Visuals */}
        <div className="lg:col-span-2 space-y-6">
          
          {/* Main Thumbnail (Glass Container) */}
          <div className={`aspect-video rounded-3xl bg-gradient-to-br ${project.color || 'from-gray-700 to-gray-900'} flex items-center justify-center shadow-2xl relative overflow-hidden group`}>
             <div className="text-9xl transform group-hover:scale-110 transition duration-500">{project.icon}</div>
             {/* Fake "Screenshot" overlay */}
             <div className="absolute inset-0 bg-black/20 group-hover:bg-transparent transition"></div>
          </div>

          {/* Screenshots Row (Mock Images) */}
          <div className="grid grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="aspect-video bg-white/10 rounded-xl hover:bg-white/20 cursor-pointer transition border border-white/10"></div>
            ))}
          </div>
        </div>

        {/* RIGHT COLUMN: Info & Actions */}
        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-xl border border-white/20 p-8 rounded-3xl shadow-xl">
            
            <span className="bg-blue-500/20 text-blue-300 px-3 py-1 rounded-full text-xs font-bold uppercase tracking-wider mb-4 inline-block">
              {project.category}
            </span>
            
            <h1 className="text-4xl font-bold mb-2">{project.title}</h1>
            <p className="text-lg text-white/60 mb-6">Created by {project.author}</p>

            {/* ACTION BUTTONS */}
            <div className="flex gap-4 mb-8">
              <button 
                className="flex-1 bg-white text-indigo-900 font-bold py-4 rounded-xl hover:bg-blue-50 transition shadow-lg hover:scale-[1.02] active:scale-[0.98]"
                onClick={() => alert(`Launching ${project.title}... (Link would open here)`)}
              >
                Launch App üöÄ
              </button>
              
              <button 
                onClick={handleLike}
                className={`px-6 rounded-xl font-bold border border-white/20 transition flex items-center gap-2 ${isLiked ? 'bg-pink-500 border-pink-500 text-white' : 'bg-white/5 hover:bg-white/10'}`}
              >
                {isLiked ? '‚ù§Ô∏è' : 'ü§ç'} {likes}
              </button>
            </div>

            <hr className="border-white/10 mb-6" />

            <h3 className="text-xl font-bold mb-3">About this Project</h3>
            <p className="text-blue-100 leading-relaxed">
              This is a placeholder description. In the real app, this text will come from the database. 
              The student has built a {project.category} that solves real-world problems using React and Firebase.
            </p>

            <div className="mt-8">
              <h3 className="text-sm font-bold text-gray-400 uppercase mb-3">Tech Stack</h3>
              <div className="flex flex-wrap gap-2">
                <span className="px-3 py-1 bg-black/30 rounded-lg text-sm border border-white/10">React</span>
                <span className="px-3 py-1 bg-black/30 rounded-lg text-sm border border-white/10">Firebase</span>
                <span className="px-3 py-1 bg-black/30 rounded-lg text-sm border border-white/10">Tailwind</span>
              </div>
            </div>

          </div>
        </div>

      </div>
    </div>
  );
}

export default ProjectDetails;
