import React, { useState } from 'react';

// Import all your pages
import Login from './pages/Login';
import StudentHome from './pages/StudentHome';
import AdminPanel from './pages/AdminPanel';
import AddProject from './pages/AddProject';
import ProjectDetails from './pages/ProjectDetails';

function App() {
  // STATE 1: Keeps track of which page to show
  // Options: 'login', 'student', 'admin', 'add-project', 'view-project'
  const [userRole, setUserRole] = useState('login');

  // STATE 2: Keeps track of which specific project was clicked
  const [selectedProject, setSelectedProject] = useState(null);

  // FUNCTION: Handles switching pages (Navigation)
  const handleNavigate = (page) => {
    setUserRole(page);
  };

  // FUNCTION: Opens a specific project detail page
  const handleViewProject = (project) => {
    setSelectedProject(project); // Remember which project was clicked
    setUserRole('view-project'); // Switch the screen
  };

  return (
    <div className="App">
      
      {/* 1. LOGIN PAGE */}
      {userRole === 'login' && (
        <Login onLogin={(role) => setUserRole(role)} />
      )}
      
      {/* 2. STUDENT HOME PAGE */}
      {userRole === 'student' && (
        <StudentHome 
          onNavigate={handleNavigate} 
          onViewProject={handleViewProject} 
        />
      )}
      
      {/* 3. ADD PROJECT WIZARD */}
      {userRole === 'add-project' && (
        <AddProject onBack={() => setUserRole('student')} />
      )}

      {/* 4. PROJECT DETAILS PAGE */}
      {/* Only show if we are in 'view-project' mode AND have a project selected */}
      {userRole === 'view-project' && selectedProject && (
        <ProjectDetails 
          project={selectedProject} 
          onBack={() => setUserRole('student')} 
        />
      )}
      
      {/* 5. ADMIN DASHBOARD */}
      {userRole === 'admin' && (
        <AdminPanel />
      )}

    </div>
  );
}

export default App;
