import React, { useState } from 'react';

function ProjectDetails({ project, onBack }) {
  
  // This state remembers if the user liked the project
  const [likes, setLikes] = useState(project.views || 0);
  const [isLiked, setIsLiked] = useState(false);

  // This function runs when you click the Heart button
  const handleLike = () => {
    if (isLiked) {
      setLikes(likes - 1);
      setIsLiked(false);
    } else {
      setLikes(likes + 1);
      setIsLiked(true);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 text-white p-6">
      
      {/* THE BACK BUTTON */}
      <button 
        onClick={onBack}
        className="mb-8 flex items-center gap-2 text-blue-200 hover:text-white transition font-bold"
      >
        ‚Üê Back to Browse
      </button>

      {/* THE LAYOUT GRID */}
      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
        
        {/* LEFT SIDE: Big Image */}
        <div className="lg:col-span-2 space-y-6">
          <div className={`aspect-video rounded-3xl bg-gray-800 shadow-2xl relative overflow-hidden group flex items-center justify-center border border-white/10`}>
             
             {/* üñºÔ∏è IMAGE DISPLAY LOGIC */}
             {project.imageUrl ? (
               <img 
                 src={project.imageUrl} 
                 alt={project.title} 
                 className="w-full h-full object-cover"
               />
             ) : (
               <div className={`w-full h-full bg-gradient-to-br ${project.color || 'from-gray-700 to-gray-900'} flex items-center justify-center`}>
                  <div className="text-9xl transform group-hover:scale-110 transition duration-500">
                    {project.icon || 'üì¶'}
                  </div>
               </div>
             )}

          </div>
        </div>

        {/* RIGHT SIDE: Title, Description, Launch Button */}
        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-xl border border-white/20 p-8 rounded-3xl shadow-xl">
            
            <span className="bg-blue-500/20 text-blue-300 px-3 py-1 rounded-full text-xs font-bold uppercase tracking-wider mb-4 inline-block">
              {project.category}
            </span>
            
            <h1 className="text-4xl font-bold mb-2">{project.title}</h1>
            <p className="text-lg text-white/60 mb-6">Created by {project.author}</p>

            {/* ACTION BUTTONS */}
            <div className="flex gap-4 mb-8">
              <button 
                className="flex-1 bg-white text-indigo-900 font-bold py-4 rounded-xl hover:bg-blue-50 transition shadow-lg hover:scale-[1.02]"
                onClick={() => {
                  if (project.link) {
                    window.open(project.link, "_blank");
                  } else {
                    alert("No link provided for this project.");
                  }
                }}
              >
                Launch App üöÄ
              </button>
              
              <button 
                onClick={handleLike}
                className={`px-6 rounded-xl font-bold border border-white/20 transition flex items-center gap-2 ${isLiked ? 'bg-pink-500 border-pink-500 text-white' : 'bg-white/5 hover:bg-white/10'}`}
              >
                {isLiked ? '‚ù§Ô∏è' : 'ü§ç'} {likes}
              </button>
            </div>

            <hr className="border-white/10 mb-6" />

            <h3 className="text-xl font-bold mb-3">About this Project</h3>
            <p className="text-blue-100 leading-relaxed whitespace-pre-wrap">
              {project.description || "No description provided."}
            </p>

          </div>
        </div>

      </div>
    </div>
  );
}

export default ProjectDetails;
