import React, { useState, useEffect } from 'react';
import { db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';

function StudentHome({ onNavigate, onViewProject, onLogout }) { 
  const [searchTerm, setSearchTerm] = useState('');
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  
  // Carousel State
  const [currentSlide, setCurrentSlide] = useState(0);

  // 1. FETCH DATA
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "projects"));
        const projectsList = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setProjects(projectsList);
        setLoading(false);
      } catch (error) {
        console.error("Error loading projects:", error);
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  // 2. FILTERING LOGIC
  const approvedProjects = projects.filter(p => p.status === 'Approved');
  
  // 3. TOP 5 ONLY for Hero Section
  const heroProjects = approvedProjects.slice(0, 5);

  const filteredProjects = approvedProjects.filter(p => 
    p.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 4. CAROUSEL CONTROLS
  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % heroProjects.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + heroProjects.length) % heroProjects.length);
  };

  // Auto-play (pauses if there are no projects)
  useEffect(() => {
    if (heroProjects.length > 0) {
      const interval = setInterval(nextSlide, 5000); // 5 seconds
      return () => clearInterval(interval);
    }
  }, [heroProjects.length]);

  // Current Hero Data
  const heroProject = heroProjects.length > 0 ? heroProjects[currentSlide] : {
    title: "Waiting for Projects...",
    description: "New submissions are being reviewed by the Admin.",
    color: "from-gray-700 to-gray-900",
    icon: "‚è≥",
    imageUrl: null
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-blue-900 text-white pb-20">
      
      {/* NAVBAR */}
      <nav className="sticky top-0 z-50 bg-white/10 backdrop-blur-md border-b border-white/10 px-6 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold tracking-tight">CampusCanvas</h1>
        
        <div className="hidden md:flex bg-black/20 rounded-full px-4 py-2 border border-white/10 w-96">
          <span className="mr-2">üîç</span>
          <input 
            type="text" 
            placeholder="Search projects..." 
            className="bg-transparent outline-none w-full text-sm placeholder-white/50"
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="flex items-center gap-4">
          <button 
            onClick={() => onNavigate('add-project')}
            className="w-10 h-10 rounded-full bg-blue-500 flex items-center justify-center font-bold hover:bg-blue-400 transition shadow-lg shadow-blue-500/50 text-white text-2xl pb-1"
          >
            +
          </button>
          
          <button onClick={onLogout} className="bg-red-500/20 text-red-300 px-4 py-2 rounded-full text-sm font-bold hover:bg-red-500 hover:text-white transition">
            Logout
          </button>
        </div>
      </nav>

      {/* HERO SECTION (CAROUSEL) */}
      <div className="max-w-7xl mx-auto mt-10 px-6">
        <div className={`relative overflow-hidden rounded-3xl p-8 md:p-12 shadow-2xl border border-white/20 h-[400px] flex items-center`}>
          
          {/* A. DYNAMIC BACKGROUND IMAGE */}
          {heroProject.imageUrl ? (
            <>
              <img 
                src={heroProject.imageUrl} 
                alt="Background" 
                className="absolute inset-0 w-full h-full object-cover transition-transform duration-1000 transform scale-105"
              />
              {/* Dark Overlay so text is readable */}
              <div className="absolute inset-0 bg-gradient-to-r from-black/90 via-black/60 to-transparent"></div>
            </>
          ) : (
            <div className={`absolute inset-0 bg-gradient-to-r ${heroProject.color || 'from-violet-600 to-indigo-600'}`}></div>
          )}

          {/* B. CONTENT */}
          <div className="relative z-10 max-w-2xl animate-fadeIn">
            <span className="bg-white/20 px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wider mb-4 inline-block backdrop-blur-md">
              Featured Top 5
            </span>
            <h2 className="text-4xl md:text-5xl font-bold mb-4 leading-tight">{heroProject.title}</h2>
            <p className="text-lg text-gray-200 mb-8 line-clamp-2">{heroProject.description}</p>
            
            {heroProjects.length > 0 && (
              <button 
                 onClick={() => onViewProject(heroProject)}
                 className="bg-white text-indigo-900 px-8 py-3 rounded-full font-bold hover:bg-blue-50 transition shadow-lg hover:scale-105 active:scale-95"
              >
                Check it out üöÄ
              </button>
            )}
          </div>

          {/* C. CONTROLS (ARROWS) */}
          {heroProjects.length > 1 && (
            <>
              <button onClick={prevSlide} className="absolute left-4 top-1/2 -translate-y-1/2 w-10 h-10 rounded-full bg-white/10 hover:bg-white/30 flex items-center justify-center text-2xl backdrop-blur-md transition">
                ‚ùÆ
              </button>
              <button onClick={nextSlide} className="absolute right-4 top-1/2 -translate-y-1/2 w-10 h-10 rounded-full bg-white/10 hover:bg-white/30 flex items-center justify-center text-2xl backdrop-blur-md transition">
                ‚ùØ
              </button>
            </>
          )}

          {/* D. INDICATORS (DOTS) */}
          <div className="absolute bottom-6 left-1/2 -translate-x-1/2 flex gap-2">
            {heroProjects.map((_, idx) => (
              <button 
                key={idx} 
                onClick={() => setCurrentSlide(idx)}
                className={`w-3 h-3 rounded-full transition-all ${idx === currentSlide ? 'bg-white w-8' : 'bg-white/30 hover:bg-white/60'}`}
              />
            ))}
          </div>

        </div>
      </div>

      {/* PROJECT GRID */}
      <div className="max-w-7xl mx-auto mt-12 px-6">
        <h3 className="text-2xl font-bold mb-6 flex items-center">üî• Trending Projects</h3>
        
        {loading ? (
           <div className="text-center py-20 text-white/50">Loading cloud data...</div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {filteredProjects.map((project) => (
              <div key={project.id} className="group bg-white/5 backdrop-blur-lg border border-white/10 rounded-2xl p-5 hover:bg-white/10 transition cursor-pointer">
                
                <div className={`h-32 rounded-xl bg-gray-800 overflow-hidden mb-4 relative group-hover:scale-105 transition duration-300 shadow-lg`}>
                  {project.imageUrl ? (
                    <img src={project.imageUrl} alt={project.title} className="w-full h-full object-cover" />
                  ) : (
                    <div className={`w-full h-full bg-gradient-to-br ${project.color || 'from-gray-500 to-gray-700'} flex items-center justify-center text-5xl`}>
                      {project.icon || 'üì¶'}
                    </div>
                  )}
                </div>

                <h4 className="text-xl font-bold truncate">{project.title}</h4>
                <p className="text-sm text-white/60 mb-4">{project.category} ‚Ä¢ by {project.author?.split('(')[0]}</p>
                <div className="flex justify-between items-center border-t border-white/10 pt-4">
                  <span className="text-xs bg-white/10 px-2 py-1 rounded">üëÄ {project.views || 0}</span>
                  <button onClick={() => onViewProject(project)} className="text-sm font-bold text-blue-300 hover:text-white transition">
                    View ‚ûú
                  </button>
                </div>
              </div>
            ))}
            
            {filteredProjects.length === 0 && (
               <div className="col-span-full text-center text-white/50 py-10">No projects match.</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default StudentHome;
